import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { GoalsService, Goal } from '../../services/goals.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-goal-form',
  templateUrl: './goal-form.component.html',
  styleUrls: ['./goal-form.component.css']
})
export class GoalFormComponent implements OnInit {
  @Input() goal: Goal | null = null;
  @Output() saved = new EventEmitter<void>();
  @Output() cancelled = new EventEmitter<void>();

  formData: Partial<Goal> = {
    title: '',
    description: '',
    startDate: '',
    dueDate: '',
    priority: 'medium',
    category: '',
    status: 'active',
    steps: []
  };

  error: string | null = null;
  loading = false;

  constructor(
    private goalsService: GoalsService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    if (this.goal) {
      // Edit mode
      this.formData = {
        title: this.goal.title,
        description: this.goal.description || '',
        startDate: this.formatDate(this.goal.startDate),
        dueDate: this.formatDate(this.goal.dueDate),
        priority: this.goal.priority,
        category: this.goal.category || '',
        status: this.goal.status,
        steps: this.goal.steps || []
      };
    }
  }

  formatDate(date: string | Date): string {
    if (!date) return '';
    const d = new Date(date);
    return d.toISOString().split('T')[0];
  }

  onSubmit() {
    if (!this.formData.title || !this.formData.startDate || !this.formData.dueDate) {
      this.error = 'Title, start date, and due date are required';
      return;
    }

    this.loading = true;
    this.error = null;

    const goalData: Goal = {
      user_id: this.authService.getCurrentUserId(),
      title: this.formData.title!,
      description: this.formData.description,
      startDate: this.formData.startDate as string,
      dueDate: this.formData.dueDate as string,
      priority: this.formData.priority || 'medium',
      category: this.formData.category,
      status: this.formData.status || 'active',
      steps: this.formData.steps || []
    };

    const operation = this.goal
      ? this.goalsService.updateGoal(this.goal._id!, goalData)
      : this.goalsService.createGoal(goalData);

    operation.subscribe({
      next: () => {
        this.loading = false;
        this.saved.emit();
      },
      error: (err) => {
        this.loading = false;
        this.error = err.error?.error || 'Failed to save goal';
        console.error('Error saving goal:', err);
      }
    });
  }

  onCancel() {
    this.cancelled.emit();
  }
}
<div class="goal-form">
  <h2>{{ goal ? 'Edit Goal' : 'Create New Goal' }}</h2>

  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="title">Title *</label>
      <input
        type="text"
        id="title"
        [(ngModel)]="formData.title"
        name="title"
        required
        maxlength="200"
        class="form-control">
    </div>

    <div class="form-group">
      <label for="description">Description</label>
      <textarea
        id="description"
        [(ngModel)]="formData.description"
        name="description"
        rows="3"
        maxlength="1000"
        class="form-control"></textarea>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label for="startDate">Start Date *</label>
        <input
          type="date"
          id="startDate"
          [(ngModel)]="formData.startDate"
          name="startDate"
          required
          class="form-control">
      </div>

      <div class="form-group">
        <label for="dueDate">Due Date *</label>
        <input
          type="date"
          id="dueDate"
          [(ngModel)]="formData.dueDate"
          name="dueDate"
          required
          class="form-control">
      </div>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label for="priority">Priority</label>
        <select
          id="priority"
          [(ngModel)]="formData.priority"
          name="priority"
          class="form-control">
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
      </div>

      <div class="form-group">
        <label for="status">Status</label>
        <select
          id="status"
          [(ngModel)]="formData.status"
          name="status"
          class="form-control">
          <option value="active">Active</option>
          <option value="completed">Completed</option>
          <option value="abandoned">Abandoned</option>
        </select>
      </div>
    </div>

    <div class="form-group">
      <label for="category">Category</label>
      <input
        type="text"
        id="category"
        [(ngModel)]="formData.category"
        name="category"
        maxlength="50"
        class="form-control">
    </div>

    <div *ngIf="error" class="error-message">
      {{ error }}
    </div>

    <div class="form-actions">
      <button type="button" class="btn-secondary" (click)="onCancel()" [disabled]="loading">
        Cancel
      </button>
      <button type="submit" class="btn-primary" [disabled]="loading">
        {{ loading ? 'Saving...' : (goal ? 'Update' : 'Create') }}
      </button>
    </div>
  </form>
</div>
