import { Component, OnInit } from '@angular/core';
import { GoalsService, Goal } from '../../services/goals.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-goals',
  templateUrl: './goals.component.html',
  styleUrls: ['./goals.component.css']
})
export class GoalsComponent implements OnInit {
  goals: Goal[] = [];
  loading = true;
  error: string | null = null;
  showForm = false;
  editingGoal: Goal | null = null;
  filterStatus: string = '';
  filterPriority: string = '';

  constructor(
    private goalsService: GoalsService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    this.loadGoals();
  }

  loadGoals() {
    this.loading = true;
    this.error = null;
    const userId = this.authService.getCurrentUserId();
    const filters: any = {};
    if (this.filterStatus) filters.status = this.filterStatus;
    if (this.filterPriority) filters.priority = this.filterPriority;

    this.goalsService.getGoals(userId, filters).subscribe({
      next: (response) => {
        this.goals = response.data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load goals';
        this.loading = false;
        console.error('Error loading goals:', err);
      }
    });
  }

  openCreateForm() {
    this.editingGoal = null;
    this.showForm = true;
  }

  openEditForm(goal: Goal) {
    this.editingGoal = { ...goal };
    this.showForm = true;
  }

  closeForm() {
    this.showForm = false;
    this.editingGoal = null;
  }

  onGoalSaved() {
    this.closeForm();
    this.loadGoals();
  }

  deleteGoal(goal: Goal) {
    if (!goal._id) return;
    if (!confirm(`Are you sure you want to delete "${goal.title}"?`)) return;

    const userId = this.authService.getCurrentUserId();
    this.goalsService.deleteGoal(goal._id, userId).subscribe({
      next: () => {
        this.loadGoals();
      },
      error: (err) => {
        this.error = 'Failed to delete goal';
        console.error('Error deleting goal:', err);
      }
    });
  }

  applyFilters() {
    this.loadGoals();
  }

  clearFilters() {
    this.filterStatus = '';
    this.filterPriority = '';
    this.loadGoals();
  }
}
<div class="goals-container">
  <div class="goals-header">
    <h1>My Goals</h1>
    <button class="btn-primary" (click)="openCreateForm()">+ New Goal</button>
  </div>

  <!-- Filters -->
  <div class="filters">
    <select [(ngModel)]="filterStatus" (change)="applyFilters()">
      <option value="">All Statuses</option>
      <option value="active">Active</option>
      <option value="completed">Completed</option>
      <option value="abandoned">Abandoned</option>
    </select>
    <select [(ngModel)]="filterPriority" (change)="applyFilters()">
      <option value="">All Priorities</option>
      <option value="low">Low</option>
      <option value="medium">Medium</option>
      <option value="high">High</option>
    </select>
    <button class="btn-secondary" (click)="clearFilters()">Clear Filters</button>
  </div>

  <!-- Loading -->
  <div *ngIf="loading" class="loading">
    <p>Loading goals...</p>
  </div>

  <!-- Error -->
  <div *ngIf="error" class="error">
    <p>{{ error }}</p>
    <button (click)="loadGoals()">Retry</button>
  </div>

  <!-- Goals List -->
  <div *ngIf="!loading && !error" class="goals-list">
    <div *ngIf="goals.length === 0" class="empty-state">
      <p>No goals found. Create your first goal to get started!</p>
    </div>

    <div *ngFor="let goal of goals" class="goal-card" [class.completed]="goal.status === 'completed'">
      <div class="goal-header">
        <div>
          <h3>{{ goal.title }}</h3>
          <span class="badge" [class]="'badge-' + goal.priority">{{ goal.priority }}</span>
          <span class="badge" [class]="'badge-' + goal.status">{{ goal.status }}</span>
        </div>
        <div class="goal-actions">
          <button class="btn-icon" (click)="openEditForm(goal)">‚úèÔ∏è</button>
          <button class="btn-icon" (click)="deleteGoal(goal)">üóëÔ∏è</button>
        </div>
      </div>

      <p *ngIf="goal.description" class="goal-description">{{ goal.description }}</p>

      <div class="goal-dates">
        <span>Start: {{ goal.startDate | date:'shortDate' }}</span>
        <span>Due: {{ goal.dueDate | date:'shortDate' }}</span>
      </div>

      <div *ngIf="goal.category" class="goal-category">{{ goal.category }}</div>

      <div *ngIf="goal.progress !== undefined" class="progress-bar">
        <div class="progress-fill" [style.width.%]="goal.progress"></div>
        <span class="progress-text">{{ goal.progress }}%</span>
      </div>
    </div>
  </div>

  <!-- Goal Form Modal -->
  <div *ngIf="showForm" class="modal-overlay" (click)="closeForm()">
    <div class="modal-content" (click)="$event.stopPropagation()">
      <app-goal-form
        [goal]="editingGoal"
        (saved)="onGoalSaved()"
        (cancelled)="closeForm()">
      </app-goal-form>
    </div>
  </div>
</div>
