import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { HabitsService, Habit } from '../../services/habits.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-habit-form',
  templateUrl: './habit-form.component.html',
  styleUrls: ['./habit-form.component.css']
})
export class HabitFormComponent implements OnInit {
  @Input() habit: Habit | null = null;
  @Output() saved = new EventEmitter<void>();
  @Output() cancelled = new EventEmitter<void>();

  formData: Partial<Habit> = {
    title: '',
    description: '',
    frequency: 'daily',
    category: '',
    status: 'active'
  };

  error: string | null = null;
  loading = false;

  constructor(
    private habitsService: HabitsService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    if (this.habit) {
      // Edit mode
      this.formData = {
        title: this.habit.title,
        description: this.habit.description || '',
        frequency: this.habit.frequency,
        category: this.habit.category || '',
        status: this.habit.status
      };
    }
  }

  onSubmit() {
    if (!this.formData.title) {
      this.error = 'Title is required';
      return;
    }

    this.loading = true;
    this.error = null;

    const habitData: Habit = {
      user_id: this.authService.getCurrentUserId(),
      title: this.formData.title!,
      description: this.formData.description,
      frequency: this.formData.frequency || 'daily',
      category: this.formData.category,
      status: this.formData.status || 'active'
    };

    const operation = this.habit
      ? this.habitsService.updateHabit(this.habit._id!, habitData)
      : this.habitsService.createHabit(habitData);

    operation.subscribe({
      next: () => {
        this.loading = false;
        this.saved.emit();
      },
      error: (err) => {
        this.loading = false;
        this.error = err.error?.error || 'Failed to save habit';
        console.error('Error saving habit:', err);
      }
    });
  }

  onCancel() {
    this.cancelled.emit();
  }
}
<div class="habit-form">
  <h2>{{ habit ? 'Edit Habit' : 'Create New Habit' }}</h2>

  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="title">Title *</label>
      <input
        type="text"
        id="title"
        [(ngModel)]="formData.title"
        name="title"
        required
        maxlength="200"
        class="form-control">
    </div>

    <div class="form-group">
      <label for="description">Description</label>
      <textarea
        id="description"
        [(ngModel)]="formData.description"
        name="description"
        rows="3"
        maxlength="1000"
        class="form-control"></textarea>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label for="frequency">Frequency</label>
        <select
          id="frequency"
          [(ngModel)]="formData.frequency"
          name="frequency"
          class="form-control">
          <option value="daily">Daily</option>
          <option value="weekly">Weekly</option>
        </select>
      </div>

      <div class="form-group">
        <label for="status">Status</label>
        <select
          id="status"
          [(ngModel)]="formData.status"
          name="status"
          class="form-control">
          <option value="active">Active</option>
          <option value="archived">Archived</option>
        </select>
      </div>
    </div>

    <div class="form-group">
      <label for="category">Category</label>
      <input
        type="text"
        id="category"
        [(ngModel)]="formData.category"
        name="category"
        maxlength="50"
        class="form-control">
    </div>

    <div *ngIf="error" class="error-message">
      {{ error }}
    </div>

    <div class="form-actions">
      <button type="button" class="btn-secondary" (click)="onCancel()" [disabled]="loading">
        Cancel
      </button>
      <button type="submit" class="btn-primary" [disabled]="loading">
        {{ loading ? 'Saving...' : (habit ? 'Update' : 'Create') }}
      </button>
    </div>
  </form>
</div>
