import { Component, OnInit } from '@angular/core';
import { DashboardService } from '../../services/dashboard.service';
import { HabitsService } from '../../services/habits.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  dashboardData: any = null;
  loading = true;
  error: string | null = null;

  constructor(
    private dashboardService: DashboardService,
    private habitsService: HabitsService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    this.loadDashboard();
  }

  loadDashboard() {
    const userId = this.authService.getCurrentUserId();
    this.loading = true;
    this.error = null;

    this.dashboardService.getDashboard(userId).subscribe({
      next: (response) => {
        this.dashboardData = response.data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load dashboard data. Make sure the backend is running on http://localhost:3000';
        this.loading = false;
        console.error('Error loading dashboard:', err);
      }
    });
  }

  toggleHabitCompletion(habit: any) {
    if (!habit._id) return;

    const userId = this.authService.getCurrentUserId();
    const today = new Date().toISOString().split('T')[0];

    if (habit.is_completed_today) {
      // Already completed, do nothing (or could implement un-logging if needed)
      return;
    }

    this.habitsService.logHabit(habit._id, userId, today).subscribe({
      next: (response) => {
        // Update local state
        habit.is_completed_today = true;
        if (response.data.current_streak !== undefined) {
          habit.current_streak = response.data.current_streak;
        }
        if (response.data.best_streak !== undefined) {
          habit.best_streak = response.data.best_streak;
        }
        if (response.data.weekly_completion_rate !== undefined) {
          habit.weekly_completion_rate = response.data.weekly_completion_rate;
        }
        // Reload dashboard to get updated best_streak
        this.loadDashboard();
      },
      error: (err) => {
        this.error = err.error?.error || 'Failed to log habit completion';
        console.error('Error logging habit:', err);
      }
    });
  }
}
<div class="container">
  <header>
    <h1>{{ 'Personal Organizer' }}</h1>
    <p class="subtitle">Your personal goals and habits tracker</p>
  </header>

  <div *ngIf="loading" class="loading">
    <p>Loading dashboard...</p>
  </div>

  <div *ngIf="error" class="error">
    <p>{{ error }}</p>
    <button (click)="loadDashboard()">Retry</button>
  </div>

  <div *ngIf="dashboardData && !loading" class="dashboard">
    <!-- Statistics Cards -->
    <div class="stats-grid">
      <div class="stat-card">
        <div class="stat-icon">üéØ</div>
        <div class="stat-value">{{ dashboardData.completed_goals_count }}</div>
        <div class="stat-label">Completed Goals</div>
      </div>

      <div class="stat-card">
        <div class="stat-icon">üî•</div>
        <div class="stat-value">{{ dashboardData.best_streak }}</div>
        <div class="stat-label">Best Streak</div>
      </div>
    </div>

    <!-- Habits Today -->
    <div class="habits-section">
      <h2>Habits for Today</h2>

      <div *ngIf="dashboardData.habits_today.length === 0" class="empty-state">
        <p>No daily habits to complete today. Create some habits to get started!</p>
      </div>

      <div class="habits-grid">
        <div *ngFor="let habit of dashboardData.habits_today" 
             class="habit-card" 
             [class.completed]="habit.is_completed_today">
          <div class="habit-header">
            <h3>{{ habit.title }}</h3>
            <button 
              class="check-button"
              [class.checked]="habit.is_completed_today"
              (click)="toggleHabitCompletion(habit)"
              [disabled]="habit.is_completed_today">
              {{ habit.is_completed_today ? '‚úì' : '‚óã' }}
            </button>
          </div>

          <p *ngIf="habit.description" class="habit-description">{{ habit.description }}</p>

          <div class="habit-stats">
            <span class="stat-badge">üî• {{ habit.current_streak }} day streak</span>
            <span class="stat-badge">‚≠ê Best: {{ habit.best_streak }}</span>
            <span class="stat-badge">üìä {{ habit.weekly_completion_rate }}% this week</span>
          </div>

          <div *ngIf="habit.category" class="habit-category">
            {{ habit.category }}
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
