import { Component, OnInit } from '@angular/core';
import { HabitsService, Habit } from '../../services/habits.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-habits',
  templateUrl: './habits.component.html',
  styleUrls: ['./habits.component.css']
})
export class HabitsComponent implements OnInit {
  habits: Habit[] = [];
  loading = true;
  error: string | null = null;
  showForm = false;
  editingHabit: Habit | null = null;
  filterStatus: string = '';
  filterFrequency: string = '';

  constructor(
    private habitsService: HabitsService,
    private authService: AuthService
  ) {}

  ngOnInit() {
    this.loadHabits();
  }

  loadHabits() {
    this.loading = true;
    this.error = null;
    const userId = this.authService.getCurrentUserId();
    const filters: any = {};
    if (this.filterStatus) filters.status = this.filterStatus;
    if (this.filterFrequency) filters.frequency = this.filterFrequency;

    this.habitsService.getHabits(userId, filters).subscribe({
      next: (response) => {
        this.habits = response.data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load habits';
        this.loading = false;
        console.error('Error loading habits:', err);
      }
    });
  }

  openCreateForm() {
    this.editingHabit = null;
    this.showForm = true;
  }

  openEditForm(habit: Habit) {
    this.editingHabit = { ...habit };
    this.showForm = true;
  }

  closeForm() {
    this.showForm = false;
    this.editingHabit = null;
  }

  onHabitSaved() {
    this.closeForm();
    this.loadHabits();
  }

  deleteHabit(habit: Habit) {
    if (!habit._id) return;
    if (!confirm(`Are you sure you want to delete "${habit.title}"?`)) return;

    const userId = this.authService.getCurrentUserId();
    this.habitsService.deleteHabit(habit._id, userId).subscribe({
      next: () => {
        this.loadHabits();
      },
      error: (err) => {
        this.error = 'Failed to delete habit';
        console.error('Error deleting habit:', err);
      }
    });
  }

  applyFilters() {
    this.loadHabits();
  }

  clearFilters() {
    this.filterStatus = '';
    this.filterFrequency = '';
    this.loadHabits();
  }
}
<div class="habits-container">
  <div class="habits-header">
    <h1>My Habits</h1>
    <button class="btn-primary" (click)="openCreateForm()">+ New Habit</button>
  </div>

  <!-- Filters -->
  <div class="filters">
    <select [(ngModel)]="filterStatus" (change)="applyFilters()">
      <option value="">All Statuses</option>
      <option value="active">Active</option>
      <option value="archived">Archived</option>
    </select>
    <select [(ngModel)]="filterFrequency" (change)="applyFilters()">
      <option value="">All Frequencies</option>
      <option value="daily">Daily</option>
      <option value="weekly">Weekly</option>
    </select>
    <button class="btn-secondary" (click)="clearFilters()">Clear Filters</button>
  </div>

  <!-- Loading -->
  <div *ngIf="loading" class="loading">
    <p>Loading habits...</p>
  </div>

  <!-- Error -->
  <div *ngIf="error" class="error">
    <p>{{ error }}</p>
    <button (click)="loadHabits()">Retry</button>
  </div>

  <!-- Habits List -->
  <div *ngIf="!loading && !error" class="habits-list">
    <div *ngIf="habits.length === 0" class="empty-state">
      <p>No habits found. Create your first habit to get started!</p>
    </div>

    <div *ngFor="let habit of habits" class="habit-card" [class.archived]="habit.status === 'archived'">
      <div class="habit-header">
        <div>
          <h3>{{ habit.title }}</h3>
          <span class="badge badge-frequency">{{ habit.frequency }}</span>
          <span class="badge" [class]="'badge-' + habit.status">{{ habit.status }}</span>
        </div>
        <div class="habit-actions">
          <button class="btn-icon" (click)="openEditForm(habit)">‚úèÔ∏è</button>
          <button class="btn-icon" (click)="deleteHabit(habit)">üóëÔ∏è</button>
        </div>
      </div>

      <p *ngIf="habit.description" class="habit-description">{{ habit.description }}</p>

      <div class="habit-stats">
        <span class="stat-badge">üî• {{ habit.current_streak || 0 }} day streak</span>
        <span class="stat-badge">‚≠ê Best: {{ habit.best_streak || 0 }}</span>
        <span class="stat-badge">üìä {{ habit.weekly_completion_rate || 0 }}% this week</span>
      </div>

      <div *ngIf="habit.category" class="habit-category">{{ habit.category }}</div>
    </div>
  </div>

  <!-- Habit Form Modal -->
  <div *ngIf="showForm" class="modal-overlay" (click)="closeForm()">
    <div class="modal-content" (click)="$event.stopPropagation()">
      <app-habit-form
        [habit]="editingHabit"
        (saved)="onHabitSaved()"
        (cancelled)="closeForm()">
      </app-habit-form>
    </div>
  </div>
</div>
